      <div className="mx-auto max-w-4xl space-y-8">
        {/* 헤더 섹션 */}
        <div className="flex items-center justify-between">
          <Link href="/invoices">
            <Button variant="ghost" size="sm">
              <ArrowLeft className="mr-2 h-4 w-4" />
              목록으로
            </Button>
          </Link>

          <div className="flex gap-2">
            <Button variant="outline" size="sm" onClick={() => window.print()}>
              <Printer className="mr-2 h-4 w-4" />
              인쇄
            </Button>
            <Button size="sm" disabled>
              <Download className="mr-2 h-4 w-4" />
              PDF 다운로드
            </Button>
          </div>
        </div>

        {/* 인보이스 내용 (print-friendly) */}
        <div className="print:shadow-none rounded-lg border bg-card p-8">
          {/* 인보이스 헤더 */}
          <div className="mb-8">
            <h1 className="text-3xl font-bold">Invoice</h1>
            <p className="text-muted-foreground mt-2">
              {invoice.invoice_number}
            </p>
          </div>

          {/* 발행자 & 클라이언트 정보 */}
          <div className="mb-8 grid gap-8 md:grid-cols-2">
            <Card>
              <CardHeader>
                <CardTitle className="text-base">발행자</CardTitle>
              </CardHeader>
              <CardContent className="space-y-1 text-sm">
                <p className="font-medium">{invoice.issuer_name}</p>
                <p className="text-muted-foreground">{invoice.issuer_email}</p>
                <p className="text-muted-foreground">{invoice.issuer_address}</p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader>
                <CardTitle className="text-base">클라이언트</CardTitle>
              </CardHeader>
              <CardContent className="space-y-1 text-sm">
                <p className="font-medium">{invoice.client_name}</p>
                <p className="text-muted-foreground">{invoice.client_email}</p>
                <p className="text-muted-foreground">{invoice.client_address}</p>
              </CardContent>
            </Card>
          </div>

          {/* 발행일 */}
          <div className="mb-8">
            <p className="text-sm text-muted-foreground">
              발행일: {new Date(invoice.issue_date).toLocaleDateString('ko-KR')}
            </p>
          </div>

          {/* 청구 항목 테이블 */}
          <div className="mb-8">
            <h2 className="mb-4 text-lg font-semibold">청구 항목</h2>
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>항목</TableHead>
                  <TableHead className="text-right">수량</TableHead>
                  <TableHead className="text-right">단가</TableHead>
                  <TableHead className="text-right">금액</TableHead>
